//router.js加载所有组件并且指定访问路径
import Vue from 'vue';
import Router from 'vue-router';
import Home from './views/Home.vue';
import About from './views/About.vue'; //1.1加载Home组件

import HomeContainer from "./components/tabbar/HomeContainer.vue"; //1.2加载MemberContainer.vue

import MemberContainer from "./components/tabbar/MemberContainer.vue";
import ShopContainer from "./components/tabbar/ShopContainer.vue";
import SearchContainer from "./components/tabbar/SearchContainer.vue";
import NewsList from "./components/news/NewsList.vue";
import NewsInfo from "./components/news/NewsInfo.vue";
import PhotoList from "./components/photos/PhotoList.vue";
import GoodsList from "./components/goods/Goodslist.vue";
import GoodsInfo from "./components/goods/GoodsInfo.vue";
Vue.use(Router);
export default new Router({
  routes: [{
    path: "/",
    redirect: "/home"
  }, //重定向
  {
    path: "/home",
    //组件访问路径   //如何访问/home <router-link to="/home">
    component: HomeContainer //对应组件

  }, {
    path: "/member",
    component: MemberContainer
  }, {
    path: "/cart",
    component: ShopContainer
  }, {
    path: "/search",
    component: SearchContainer
  }, {
    path: "/home/news",
    component: NewsList
  }, {
    path: "/home/newsinfo/:id",
    component: NewsInfo
  }, {
    path: "/home/photolist",
    component: PhotoList
  }, {
    path: "/home/goodslist",
    component: GoodsList
  }, {
    path: "/home/goodsinfo/:id",
    component: GoodsInfo,

    /*传递命名的路由*/
    name: "goodsinfo"
  }]
});
